<script>
  import PapaParse from 'papaparse';
  export let data;
  export let filename = 'filename';
  export let type = 'link';
  export let bom = 2;
  function download(data, filename, bom) {
    const bomCode = bom ? '\ufeff' : '';
    let csvContent = null;
    if (typeof data === 'object') {
      csvContent = PapaParse.unparse(data);
    } else {
      csvContent = data;
    }
    const csvData = new Blob([`${bomCode}${csvContent}`], {
      type: 'text/csv;charset=utf-8;'
    });
    let csvURL = null;
    if (navigator.msSaveBlob) {
      csvURL = navigator.msSaveBlob(csvData, `${filename}.csv`);
    } else {
      csvURL = window.URL.createObjectURL(csvData);
    }
    const link = document.createElement('a');
    link.href = csvURL;
    link.setAttribute('download', `${filename}.csv`);
    link.click();
    link.remove();
  }
</script>

{#if type === 'link'}
  <button class="bg-indigo-500  text-white active:bg-indigo-600 hover:bg-purple-400 text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
    on:click={download(data, filename, bom)}>
    <slot />
  </button>
{:else}
  <button on:click={download(data, filename, bom)}>
    <slot />
  </button>
{/if}

